import { useEffect, useContext } from "react";
import Cookie from "js-cookie"

import { Button, notification } from "antd"
import { StoreContext } from "../store"
import { CART_ADD_ITEM } from "../utils/constants"
import { CartIcon } from "./Icons";
import { ShoppingCartOutlined } from '@ant-design/icons';


export default function AddToCart({ product, qty }) {
  const { state: { cartItems }, dispatch } = useContext(StoreContext);

  const openNotification = () => {
    notification.open({
      message: 'Shopping Notification',
      description:
        `${qty} ${qty > 1 ? "pieces" : "piece"} of ${product.name} ${qty > 1 ? "have" : "has"} been added to your cart.`,
        icon: <ShoppingCartOutlined style={{ color: '#108ee9' }} />,
      onClick: () => {
        console.log('Notification Clicked!');
      },
      placement: 'bottomRight'
    });
  };

  const addToCart = () => {
    openNotification();
    dispatch({
      type: CART_ADD_ITEM,
      payload: {
        id: product.id,
        name: product.name,
        image: product.image,
        price: product.price,
        countInStock: product.countInStock,
        qty,
      },
    });
  };

  useEffect(()=>{
    Cookie.set("cartItems", JSON.stringify(cartItems));
   }, [cartItems])

  return (
    <Button type="primary" className="btn-tocar" onClick={addToCart}>
      <CartIcon size={20} />
      <span style={{ marginLeft: 12 }}>Add To Shopping Bag</span>
    </Button>
  );
}
